import axios from 'axios'
import React, { useState } from 'react'

const Login = () => {
 
  const[data,setdata]=useState()
  const[success,setsuccess]=useState('')

  let handlechange =(event)=>{
    setdata({...data,[event.target.name]:event.target.value})
    
  }

  let handlesubmit=(event)=>{
    event.preventDefault()
    let response=axios.post('http://localhost:4000/admin/login',data)
  }
  

  return (
    <div className='flex items-center justify-center p-10 bg-blue-200 '>
    <div className='rounded bg-white p-3 w-auto'>
      <h2 className='text-xl font-bold mb-2'>Login</h2>
  <form onSubmit={handlesubmit} className='flex flex-col'>
    <div className='mb-3 flex flex-col'>
       <label>Username</label>
         <input
           type='text' 
           onChange={handlechange} 
           name='username' 
           placeholder='Enter Username'
           className='border'/>
    </div>
     <div className='mb-3 flex flex-col'>
        <label>Password</label>
        <input 
          type='password' 
          onChange={handlechange} 
          name='password' 
          placeholder='Enter Password'
          className='border'/>
     </div>
      <button className='rounded p-1 bg-blue-400 text-white w-52'>Login</button>
  </form>
  </div>
</div>
  )
}

export default Login
