import React, { useState } from 'react'
import axios from 'axios'
import { useEffect } from 'react'
import { Link } from 'react-router-dom'
import { Toast, toast } from 'react-toastify'


const Register = () => {
  const [data,setdata]=useState('')

  const[done,setdone]=useState(true)
  const[success,setsuccess]=useState()

  let handlechange=(event)=>{
    setdata({...data,[event.target.name]:event.target.value})
    console.log(data);
  }
    
  let handlesubmit=async(event)=>{
      event.preventDefault()
      let newdata={
        ...data
      }
      let response= await axios.post('http://localhost:4000/add',newdata)
      console.log(response.data.status);
      setdone(!done)
      if(response.data.status){
        setsuccess(true)
      }
      else{
        toast('error')
        setsuccess('true')
      }
  }

  return (
    <div className='flex items-center justify-center bg-zinc-400'>
      <div className='rounded bg-white p-3 w-25'>
        <h2>Register</h2>
    <form onSubmit={handlesubmit} className='flex flex-col items-center justify-center '>
        <label><strong>Username</strong></label>
        <input type='text' onChange={handlechange} name='username' placeholder='Enter Username'/>
        <label><strong></strong></label>
        <input type='email' onChange={handlechange} name='email' placeholder='Enter Email'/>
        <label>Password</label>
        <input type='password' onChange={handlechange} name='password' placeholder='Enter Password'/>
        <input type='submit' value='Signup' />
    </form>
    </div>
</div>

  )
}

export default Register
