import axios from 'axios'
import React, { useState } from 'react'

const Login = () => {
 
  const[data,setdata]=useState()
  const[success,setsuccess]=useState(false)
  const [passwordVisible, setPasswordVisible] = useState(false);


  let handlechange =(event)=>{
    setdata({...data,[event.target.name]:event.target.value})
    
  }

  let handlesubmit=async(event)=>{
    event.preventDefault()
    let response=await axios.post('http://localhost:4000/admin/login',data)
    console.log(response.data.status);
    if(response.data.status){
      console.log('succesfull');
    }
    else{
      console.log('unsuccesful');
      setsuccess(true)
    }

    
  }

  const togglePasswordVisibility = () => {
    setPasswordVisible(!passwordVisible);
  };
  

  return (
    <div className='flex items-center mt-28 justify-center bg-blue-300 '>
    <div className='rounded bg-white p-3 w-auto'>
      <h2 className='text-xl font-bold mb-2'>Login</h2>
  <form onSubmit={handlesubmit} className='flex flex-col'>
    <div className='mb-3 flex flex-col'>
       <label>Username</label>
         <input
           type='text' 
           onChange={handlechange} 
           name='username' 
           placeholder='Enter Username'
           className='border'/>
    </div>
     <div className='mb-3 flex flex-col'>
        <label>Password</label>
        <input 
          type={passwordVisible ? 'text' : 'password'}
          onChange={handlechange} 
          name='password' 
          placeholder='Enter Password'
          className='border'/>
           <span clas onClick={togglePasswordVisibility}>{passwordVisible ? 'üëÅÔ∏è' : 'üëÅÔ∏è'}</span>
     </div>
     {success &&
     
     <h2 className='text-red-500'>invalid username or password!</h2>
     }
      <button type='submit' className='rounded p-1 bg-blue-400 text-white w-52'>Login</button>
  </form>
  </div>
</div>
  )
}

export default Login
